import React, { useEffect } from 'react';
import Header from '../public/componets/Header';
import Profile from '../public/components/Profile';
import Skills from '../public/components/Skills';
import Certifications from './components/Certifications';
import Projects from './components/Projects';
import Footer from './components/Footer';
import useScrollToSection from './hooks/useScrollToSection';

const App = () => {
  const scrollToSection = useScrollToSection();

  // 페이지 로드 시 URL 해시가 있으면 해당 섹션으로 부드럽게 스크롤
  useEffect(() => {
    const hash = window.location.hash;
    if (hash) {
      const sectionId = hash.substring(1); // #을 제거
      setTimeout(() => {
        scrollToSection(sectionId);
      }, 100);
    }
  }, [scrollToSection]);

  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="flex-grow">
        <Profile />
        <Skills />
        <Certifications />
        <Projects />
      </main>
      <Footer />
    </div>
  );
};

export default App;
